template.value:
ext {
    mapstructVersion = '1.2.0.Final'
}

// TODO: Add `mapstruct.defaultComponentModel=spring` as an annotation processor argument
// in the annotation processors section of intellij Annotation processor settings
// That way, intellij builtin compiler also gets the same arguments as below
compileJava {
    dependsOn processResources
    options.compilerArgs = [
            '-Amapstruct.suppressGeneratorTimestamp=true',
            '-Amapstruct.suppressGeneratorVersionInfoComment=true',
            '-Amapstruct.defaultComponentModel=spring'
    ]
}

dependencies {
    // lombok + mapstruct
    compileOnly("org.mapstruct:mapstruct-processor:${mapstructVersion}")
    compileOnly("org.mapstruct:mapstruct-jdk8:${mapstructVersion}")
    compileOnly('org.projectlombok:lombok')

    annotationProcessor("org.projectlombok:lombok")
    annotationProcessor("org.mapstruct:mapstruct-processor:${mapstructVersion}")
}
